// ============================================
// INPUT VALIDATION & SANITIZATION
// ============================================

const items = $input.all();
const validated = [];

for (const item of items) {
  const data = item.json.body || item.json;
  const errors = [];

  // ===== EXTRACT DEBUG FLAG =====
  const DEBUG = data.debug === true;

  if (DEBUG) {
    console.log('\nüêõ DEBUG MODE ACTIVATED via webhook request\n');
  }

  // ===== VALIDATE TERMIN =====
  if (!data.termin) {
    errors.push('Pflichtfeld fehlt: termin');
  } else {
    // Validate termin.datum
    if (!data.termin.datum) {
      errors.push('Pflichtfeld fehlt: termin.datum');
    } else {
      const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
      if (!dateRegex.test(data.termin.datum)) {
        errors.push('Ung√ºltiges Datumsformat f√ºr termin.datum. Erwartet: YYYY-MM-DD');
      } else {
        // Check if date is in the future
        const appointmentDate = new Date(data.termin.datum);
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        if (appointmentDate < today) {
          errors.push('Termin-Datum muss in der Zukunft liegen');
        }
      }
    }

    // Validate termin.zeit_von
    if (!data.termin.zeit_von) {
      errors.push('Pflichtfeld fehlt: termin.zeit_von');
    } else {
      const timeRegex = /^([01]\d|2[0-3]):[0-5]\d$/;
      if (!timeRegex.test(data.termin.zeit_von)) {
        errors.push('Ung√ºltiges Zeitformat f√ºr termin.zeit_von. Erwartet: HH:MM');
      }
    }

    // Validate termin.zeit_bis
    if (!data.termin.zeit_bis) {
      errors.push('Pflichtfeld fehlt: termin.zeit_bis');
    } else {
      const timeRegex = /^([01]\d|2[0-3]):[0-5]\d$/;
      if (!timeRegex.test(data.termin.zeit_bis)) {
        errors.push('Ung√ºltiges Zeitformat f√ºr termin.zeit_bis. Erwartet: HH:MM');
      }
    }

    // Validate time logic (zeit_bis must be after zeit_von)
    if (data.termin.zeit_von && data.termin.zeit_bis) {
      const timeFrom = data.termin.zeit_von.split(':').map(Number);
      const timeTo = data.termin.zeit_bis.split(':').map(Number);
      const minutesFrom = timeFrom[0] * 60 + timeFrom[1];
      const minutesTo = timeTo[0] * 60 + timeTo[1];

      if (minutesTo <= minutesFrom) {
        errors.push('termin.zeit_bis muss nach termin.zeit_von liegen');
      }
    }
  }

  // ===== VALIDATE ORT =====
  if (!data.ort) {
    errors.push('Pflichtfeld fehlt: ort');
  } else if (typeof data.ort !== 'string' || data.ort.trim().length === 0) {
    errors.push('ort muss ein nicht-leerer String sein');
  }

  // ===== CHECK FOR ERRORS =====
  if (errors.length > 0) {
    validated.push({
      json: {
        status: 'error',
        error_type: 'validation_error',
        message: 'Eingabe-Validierung fehlgeschlagen',
        errors: errors,
        received_data: data,
        timestamp: new Date().toISOString(),
        _debug: DEBUG
      }
    });
  } else {
    // Generate unique ID for this request
    const requestId = `flight_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    validated.push({
      json: {
        request_id: requestId,
        termin: {
          datum: data.termin.datum,
          zeit_von: data.termin.zeit_von,
          zeit_bis: data.termin.zeit_bis
        },
        ort: data.ort.trim(),
        timestamp: new Date().toISOString(),
        validation_status: 'passed',
        _debug: DEBUG
      }
    });
  }
}

return validated;

