// ============================================
// DEBUG LOGGER - RAW AGENT RESPONSE
// ============================================

const items = $input.all();
const validatedData = $('Validate Input').first().json;
const DEBUG = validatedData._debug || false;

if (DEBUG) {
  console.log('\n'.repeat(3));
  console.log('='.repeat(80));
  console.log('üîç DEBUG: RAW AGENT RESPONSE');
  console.log('='.repeat(80));
  console.log('Timestamp:', new Date().toISOString());
  console.log('Request ID:', validatedData.request_id);
  console.log('Items count:', items.length);
  console.log('-'.repeat(80));

  items.forEach((item, index) => {
    console.log(`\nAgent Response ${index + 1}:`);
    console.log(JSON.stringify(item.json, null, 2));
  });

  console.log('='.repeat(80));
  console.log('\n'.repeat(2));
}

// Pass through unchanged
return items;

// ============================================
// PARSE & VALIDATE AGENT RESPONSE
// ============================================

const items = $input.all();
const originalData = $('Validate Input').first().json;
const DEBUG = originalData._debug || false;
const parsed = [];

if (DEBUG) {
  console.log('\nüîç DEBUG: Starting to parse agent response...');
}

for (const item of items) {
  let agentResponse = item.json;

  try {
    // Extract response text if needed
    let responseText = '';

    if (typeof agentResponse === 'string') {
      responseText = agentResponse;
    } else if (agentResponse.output) {
      responseText = agentResponse.output;
    } else if (agentResponse.text) {
      responseText = agentResponse.text;
    } else if (agentResponse.response) {
      responseText = agentResponse.response;
    } else {
      // Assume it's already parsed JSON
      responseText = JSON.stringify(agentResponse);
    }

    // Clean response text
    responseText = responseText.trim();

    // Remove markdown code blocks if present
    responseText = responseText.replace(/```json\s*/g, '').replace(/```\s*/g, '');

    // Try to find JSON object in text
    let jsonMatch = responseText.match(/\{[\s\S]*\}/); 
    let parsedResponse;

    if (jsonMatch) {
      parsedResponse = JSON.parse(jsonMatch[0]);
    } else {
      parsedResponse = JSON.parse(responseText);
    }

    // ===== VALIDATE RESPONSE STRUCTURE =====
    if (!parsedResponse.status) {
      throw new Error('Missing required field: status');
    }

    const validStatuses = ['success', 'no_flights', 'error'];
    if (!validStatuses.includes(parsedResponse.status)) {
      throw new Error(`Invalid status: ${parsedResponse.status}. Must be one of: ${validStatuses.join(', ')}`);
    }

    if (!Array.isArray(parsedResponse.flights)) {
      throw new Error('Missing or invalid field: flights (must be an array)');
    }

    // ===== VALIDATE FLIGHT DATA IF SUCCESS =====
    if (parsedResponse.status === 'success' && parsedResponse.flights.length > 0) {
      for (const flight of parsedResponse.flights) {
        if (!flight.outbound || !flight.return) {
          throw new Error('Each flight must have outbound and return');
        }

        // Validate required fields
        const requiredOutbound = ['from', 'to', 'departure_time', 'arrival_time', 'airline'];
        const requiredReturn = ['from', 'to', 'departure_time', 'arrival_time', 'airline'];

        for (const field of requiredOutbound) {
          if (!flight.outbound[field]) {
            throw new Error(`Missing outbound field: ${field}`);
          }
        }

        for (const field of requiredReturn) {
          if (!flight.return[field]) {
            throw new Error(`Missing return field: ${field}`);
          }
        }
      }
    }

    if (DEBUG) {
      console.log('\n‚úÖ DEBUG: Successfully parsed agent response');
      console.log('Status:', parsedResponse.status);
      console.log('Flights found:', parsedResponse.flights?.length || 0);
    }

    // ===== BUILD SUCCESS RESPONSE =====
    parsed.push({
      json: {
        request_id: originalData.request_id,
        status: parsedResponse.status,
        flights: parsedResponse.flights || [],
        search_summary: parsedResponse.search_summary || {},
        original_request: {
          termin: originalData.termin,
          ort: originalData.ort
        },
        agent_raw_response: responseText.substring(0, 1000),
        timestamp: new Date().toISOString(),
        parsed_successfully: true
      }
    });

  } catch (error) {
    if (DEBUG) {
      console.log('\n‚ùå DEBUG: Failed to parse agent response');
      console.log('Error:', error.message);
      console.log('Raw response:', JSON.stringify(agentResponse).substring(0, 500));
    }

    // ===== PARSING FAILED =====
    parsed.push({
      json: {
        request_id: originalData.request_id,
        status: 'error',
        error_type: 'parsing_error',
        error_message: `Failed to parse agent response: ${error.message}`,
        flights: [],
        original_request: {
          termin: originalData.termin,
          ort: originalData.ort
        },
        agent_raw_response: JSON.stringify(agentResponse).substring(0, 500),
        timestamp: new Date().toISOString(),
        parsed_successfully: false
      }
    });
  }
}

return parsed;

